import React, { useState, useEffect } from "react";
import NavbarT from "../Component/NavbarT";
import Navbar from "../Component/Navbar";
import "../css/createaproblem.css";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const type = sessionStorage.getItem("mem_type");
const mem_id = sessionStorage.getItem("mem_id");

const CreateAProblem = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [problemCount, setProblemCount] = useState(0);
  const [problemType, setProblemType] = useState("주관식");
  const [difficulty, setDifficulty] = useState(1);
  const [question, setQuestion] = useState("");
  const [ex1, setEx1] = useState(["", "", "", ""]);
  const [ex2, setEx2] = useState(["", "", "", ""]);
  const [ex3, setEx3] = useState(["", "", "", ""]);
  const [ex4, setEx4] = useState(["", "", "", ""]);
  const [correctAnswer, setCorrectAnswer] = useState(null);

  useEffect(() => {
    if (location.state) {
      setProblemCount(location.state.problemCount);
    }
  }, [location.state]);

  const incrementProblemCount = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8081/addProblem",
        {
          memId: mem_id,
          problemType,
          difficulty,
          question,
          choices,
          correctAnswer,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      if (response.status === 200) {
        setProblemCount((prevCount) => prevCount + 1);
        alert("문제가 성공적으로 저장되었습니다.");
      } else {
        alert("문제 저장에 실패했습니다.");
      }
    } catch (error) {
      console.error("Error saving problem:", error);
      alert("문제 저장 중 오류가 발생했습니다.");
    }
  };

  const handleNavigateWQ = () => {
    navigate("/wq"); // "/wq"는 실제로 "wq" 페이지의 경로에 해당하는 곳으로 바꿔야 합니다.
  };

  const handleChoiceChange = (index, value) => {
    const newChoices = [...choices];
    newChoices[index] = value;
    setChoices(newChoices);
  };

  return (
    <div>
      {type === "1" ? <NavbarT /> : <Navbar />}
      <h2 className="createproblem-title">· 직접 문제 생성</h2>
      <div>
        <table className="createproblem-table">
          <tbody>
            <tr>
              <th className="createproblem-th1" colSpan={2}>
                문제 유형
              </th>
              <td className="createproble-radio">
                <input
                  type="radio"
                  className="Short"
                  name="problemType"
                  checked={problemType === "주관식"}
                  onChange={() => setProblemType("주관식")}
                />
                주관식
                <input
                  type="radio"
                  className="choice"
                  name="problemType"
                  checked={problemType === "객관식"}
                  onChange={() => setProblemType("객관식")}
                />
                객관식
              </td>
            </tr>
            <hr />
            <tr>
              <th className="createproblem-th2" colSpan={2}>
                난이도
              </th>
              <td>
                <input
                  type="number"
                  placeholder="난이도를 선택하세요"
                  min={1}
                  max={5}
                  value={difficulty}
                  onChange={(e) => setDifficulty(Number(e.target.value))}
                />
              </td>
            </tr>
            <hr />
            <tr>
              <th className="createproblem-th2" colSpan={2}>
                출제문항수
              </th>
              <td>
                <p className="createproblem-p">
                  현재 {problemCount}문제가 저장되었습니다.
                </p>
              </td>
            </tr>
            <hr />
            <tr>
              <th className="createproblem-th3" colSpan={2}>
                문제
              </th>
              <td>
                <input
                  type="text"
                  className="createproblem-questions"
                  placeholder="문제를 입력해주세요."
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                />
                {problemType === "객관식" && (
                  <>
                    {choices.map((choice, index) => (
                      <div key={index}>
                        <input
                          type="text"
                          name={`createproblem-a${index}`}
                          placeholder="보기를 입력해주세요."
                          value={choice}
                          onChange={(e) =>
                            handleChoiceChange(index, e.target.value)
                          }
                        />
                        <input
                          type="radio"
                          name="correctAnswer"
                          onChange={() => setCorrectAnswer(index)}
                        />
                      </div>
                    ))}
                  </>
                )}
              </td>
            </tr>
          </tbody>
        </table>
        <br />
        <br />
        <br />
        <div className="btn-container">
          <button className="createproblem-btn" onClick={incrementProblemCount}>
            문제저장
          </button>
          <button className="createproblem-btn2" onClick={handleNavigateWQ}>
            저장된 문제보기
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateAProblem;
