import React, { useState, useEffect } from "react";
import axios from "axios";
import NavbarT from "../Component/NavbarT";
import { Bar } from "react-chartjs-2";
import { Chart, registerables } from "chart.js";
import { useNavigate } from "react-router-dom";
import "../css/infostudent.css";
import "../css/chart.css";

Chart.register(...registerables);

const calculateScore = (answer, answerCheck) => {
  const answerObj = JSON.parse(answer);
  const answerCheckObj = JSON.parse(answerCheck);

  let totalQuestions = 0;
  let correctAnswers = 0;

  for (const key in answerObj) {
    totalQuestions++;
    if (answerObj[key] === answerCheckObj[key]) {
      correctAnswers++;
    }
  }

  return (correctAnswers / totalQuestions) * 100;
};

const ScoreChart = ({ data }) => {
  const averageScore =
    data.reduce((sum, item) => sum + item.score, 0) / data.length;

  const chartData = {
    labels: data.map((item) => item.date),
    datasets: [
      {
        label: "점수",
        data: data.map((item) => item.score),
        backgroundColor: "#239aff",
        borderColor: "#239aff",
        borderWidth: 2,
      },
    ],
  };

  const options = {
    scales: {
      x: {
        ticks: {
          maxRotation: 0,
          minRotation: 0,
          font: {
            size: 14,
          },
          color: "#239aff",
        },
      },
      y: {
        beginAtZero: true,
        max: 100,
      },
    },
    maintainAspectRatio: true,
  };

  return (
    <div>
      <h2 className="chart-title">학생 성적 보기</h2>
      <br></br>
      <div className="chart-box">
        <div className="chart">
          <Bar data={chartData} options={options} />
          <h3 className="chart-h3">평균 점수: {averageScore.toFixed(2)}</h3>
        </div>
      </div>
    </div>
  );
};

const Infostudent = () => {
  const [type, setType] = useState(sessionStorage.getItem("mem_type"));
  const [memId, setMemId] = useState(sessionStorage.getItem("mem_id"));
  const [name, setName] = useState(sessionStorage.getItem("mem_name"));
  const [address, setAddress] = useState(sessionStorage.getItem("mem_address"));
  const [number, setNumber] = useState(sessionStorage.getItem("mem_number"));
  const [email, setEmail] = useState(sessionStorage.getItem("mem_email"));

  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post("http://localhost:8081/getTests", {
          mem_id: memId,
        });

        const formattedData = response.data.map((item) => {
          const date = new Date(item.submitted_at);
          const formattedDate = date
            .toLocaleDateString("ko-KR", {
              year: "numeric",
              month: "2-digit",
              day: "2-digit",
            })
            .replace(/\./g, "")
            .replace(/\s/g, ""); // YYYYMMDD 형식으로 변환

          const score = calculateScore(item.answer, item.answer_check);

          return {
            date: formattedDate,
            workbookName: item.workbookName,
            score: score,
          };
        });

        setChartData(formattedData);
        console.log("응답", response.data);
      } catch (error) {
        console.error("데이터를 가져오는 중 오류 발생:", error);
      }
    };

    fetchData(); // 데이터 가져오는 함수 호출
  }, [memId]);

  const average =
    chartData.reduce((a, b) => a + b.score, 0) / chartData.length || 0;

  const navigate = useNavigate();

  const handleBack = () => {
    navigate("/teacher");
  };

  const handleSave = () => {
    // 여기에 수정된 데이터를 서버에 전송하는 코드를 추가
    const updatedInfo = {
      memId,
      name,
      address,
      number,
      email,
    };

    alert("정보가 저장되었습니다.");
  };

  return (
    <div>
      <header>
        <NavbarT />
      </header>
      <br />
      <h2 className="titleText">· 내 학생관리</h2>
      <br />
      <br />
      <br />
      <br />
      <h3 className="stuName">{name}</h3>
      <div className="infoBody">
        <div className="doneHomework">
          <th>· 푼 문제집{"(100%)"}</th>
          <br />
          <br />
          {chartData.map((item, index) => (
            <tr key={index}>{`${item.date} - ${item.workbookName}`}</tr>
          ))}
        </div>
        <div className="lookEasy"></div>
      </div>
      <br />
      <div className="infoEdit"></div>
      <br />
      <br />
      <button className="is-btn" onClick={handleBack}>
        확인
      </button>
      <br />
      <br />
    </div>
  );
};

export default Infostudent;
