import React, { useEffect, useState } from "react";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";
import "../css/studyrecord.css";
import Navbar from "../Component/Navbar";
// import Chart from "../Component/Chart"; // 차트 컴포넌트 주석 처리

const StudyRecord = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const examInfo = location.state?.examInfo || {};
  const gradingResults = location.state?.gradingResults || [];
  const score = location.state?.score || 0;
  const memId = sessionStorage.getItem("mem_id"); // 세션에서 mem_id 가져오기
  const [chartData, setChartData] = useState([]);

  const selectedQuestions = examInfo.selectedQuestions || [];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post("http://localhost:8081/getTests", {
          mem_id: memId,
        });

        const calculateScore = (studentAnswer, answerKey) => {
          if (!studentAnswer || !answerKey) {
            console.error("studentAnswer 또는 answerKey가 없습니다.");
            return 0; // 또는 다른 기본값으로 설정
          }

          const studentAnswers = JSON.parse(studentAnswer);
          const answerKeys = JSON.parse(answerKey);
          console.log("answer" + studentAnswer + "answerkey" + answerKey);
          let correctAnswers = 0;
          const totalQuestions = Object.keys(answerKeys).length;

          for (let key in studentAnswers) {
            if (studentAnswers[key] === answerKeys[key]) {
              correctAnswers++;
            }
          }

          return ((correctAnswers / totalQuestions) * 100).toFixed(2);
        };

        const formattedData = response.data.map((item) => {
          const date = new Date(item.submitted_at);
          const formattedDate = date
            .toLocaleDateString("ko-KR", {
              year: "numeric",
              month: "2-digit",
              day: "2-digit",
            })
            .replace(/\./g, "")
            .replace(/\s/g, "");

          const score = calculateScore(item.answer, item.answer_check);

          return {
            date: formattedDate,
            workbookName: item.workbook_name,
            score: parseFloat(score), // 소수점 이하 2자리로 제한된 점수를 숫자로 변환
          };
        });

        setChartData(formattedData);
        console.log("응답", response.data);
      } catch (error) {
        console.error("데이터를 가져오는 중 오류 발생:", error);
      }
    };

    if (memId) {
      fetchData();
    } else {
      console.error("mem_id is null or undefined");
    }
  }, [memId]);

  const renderIncorrectQuestions = () => {
    return selectedQuestions
      .filter((question, index) => gradingResults[index] === "x")
      .map((question, index) => (
        <div key={index} className="incorrect-question">
          <p>{question.content}</p>
          <p>정답: {question.correctAnswer}</p>
        </div>
      ));
  };

  const gostudyroom = () => {
    navigate("/studyRoom");
  };

  return (
    <div>
      <Navbar />
      <h2 className="studdyrecord-title">· 공부기록</h2>
      <br />
      <h3 className="sr-h3">· 시험 날짜</h3>
      <div className="srdate-box">
        <p>
          {examInfo.startDate} ~ {examInfo.endDate}
        </p>
      </div>

      <h3 className="sr-h3">
        · 내 점수: {score} / {selectedQuestions.length * 5}
      </h3>
      <hr className="sr-hr" />
      <div className="sr-box1">
        <h3 className="sr-h3">· 틀린 문제 보기</h3>
        <div className="mark-x-box">{renderIncorrectQuestions()}</div>
      </div>
      <br />
      <h3 className="sr-h3">· 최근 성적 한 눈에 보기</h3>
      <hr className="sr-hr" />
      <br />
      <div className="chart-container">
        <div className="chart">
          {chartData.length > 0 ? (
            <div>Chart Placeholder</div>
          ) : (
            <p>데이터를 불러오는 중...</p>
          )}
        </div>
      </div>
      <br />
      <button className="sr-btn" onClick={gostudyroom}>
        확인
      </button>
    </div>
  );
};

export default StudyRecord;
