import React, { useEffect, useState } from "react";
import axios from "axios";
import NavbarT from "../Component/NavbarT";
import Navbar from "../Component/Navbar";
import "../css/namelist.css";
import { useLocation, useNavigate } from "react-router-dom";

const type = sessionStorage.getItem("mem_type");

const NameList = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const {
    selectedAnswers = [],
    examInfo = {},
    mem_id,
    mem_name,
  } = location.state || {};

  const [students, setStudents] = useState([]);

  useEffect(() => {
    if (type === "1") {
      axios
        .post("http://localhost:8081/students", { mem_type: "0" })
        .then((response) => {
          const students = response.data;
          console.log(response.data);
          axios
            .post("http://localhost:8081/filteredStudents", students)
            .then((filteredResponse) => {
              setStudents(filteredResponse.data);
            })
            .catch((error) => {
              console.error(
                "필터링된 학생 정보를 불러오는 중 오류 발생:",
                error
              );
            });
        })
        .catch((error) => {
          console.error("학생 정보를 불러오는 중 오류 발생:", error);
        });
    }
  }, [type]);

  const handleSelectStudent = (student) => {
    navigate("/markpage", {
      state: {
        studentName: student.mem_name,
        studentId: student.mem_id,
        selectedAnswers,
        student,
        examInfo,
        mem_id,
        mem_name,
      },
    });
  };

  return (
    <div>
      {type === "1" ? <NavbarT /> : <Navbar />}
      <br />
      <h2 className="titleText">·문제 제출 학생 명단</h2>
      <br />
      <br />
      <div className="namelist-container">
        <div className="namelist-box">
          <h2 className="namelist-title">학생 목록</h2>
          <ul>
            {students.map((student) => (
              <li className="namelist-li" key={student.id}>
                <button
                  className="namelist-btn"
                  onClick={() => handleSelectStudent(student)}
                >
                  {student.mem_name}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default NameList;
