import React, { useEffect, useState } from "react";
import axios from "axios";
import NavbarT from "../Component/NavbarT";
import "../css/namelist.css";
import { useNavigate, useLocation } from "react-router-dom";

const NameList = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { examInfo } = location.state || {};
  const [students, setStudents] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(
          "http://localhost:8081/getStudentsByWorkbookName",
          examInfo.work_name // examInfo.work_name에 workbook 이름이 들어 있다고 가정합니다.
        );
        setStudents(response.data);
      } catch (error) {
        console.error("학생 정보를 불러오는 중 오류 발생:", error);
      }
    };

    if (examInfo && examInfo.work_name) {
      fetchData();
    }
  }, [examInfo]);

  const handleSelectStudent = (student) => {
    navigate("/markpage", {
      state: {
        studentName: student.mem_name,
        studentId: student.mem_id,
        examInfo,
      },
    });
  };

  // Filter students whose names match workbook_name and apply blue border if submitted_at exists
  const filteredStudents = students.filter((student) => {
    if (examInfo && examInfo.work_name) {
      const workbookName = examInfo.work_name;
      return student.workbook_name === workbookName;
    }
    return true;
  });

  return (
    <div>
      <NavbarT />
      <br />
      <h2 className="titleText">· 문제 제출 학생 명단</h2>
      <br />
      <br />
      <div className="namelist-container">
        <div className="namelist-box">
          <h2 className="namelist-title">학생 목록</h2>
          <ul>
            {filteredStudents.map((student) => (
              <li
                className={`namelist-li ${
                  student.submitted_at ? "blue-border" : "red-border"
                }`}
                key={student.mem_id} // 고유한 key 속성을 부여합니다.
                onClick={() => handleSelectStudent(student)}
              >
                {student.mem_name}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default NameList;
